    const SingleDog = (props) => {
        // To use useParams, you need to invoke the function useParams and use object destructuring to pull the specific parameter name off of the object. 
        const { id } = useParams(); 

        function filterDogs() {
            let newDogs = [];
            for (let i=0; i < props.dogData.length; i++ ) {
                if (props.dogData[i].id == id) {
                    return props.dogData[i];
                }
            }
        }

        let currDog = filterDogs() 
        console.log(currDog)
        return (
            <section className = "dog-info">
